// ============================================
// EDIT THESE VALUES TO CONFIGURE YOUR TEST
// ============================================

const testConfig = {
  // Client normalized name - must be lowercase with underscores
  // This must match what Pre-Chunk Zero would generate
  // Example: "Villa Martens" -> "villa_martens"
  client_normalized: 'villa_martens',

  // Google Drive folder ID containing test files
  // Updated to use the correct test folder ID from Sway's request
  testFolderId: '1GQcFD61eaWgHwXZGxfzY2h_-boyG6IHa',

  // Dummy client email for testing
  clientEmail: 'sway@oloxa.ai',

  // Spreadsheet ID for Test_Results logging
  spreadsheetId: '12N2C8iWeHkxJQ2qz7m3aTyZw3X1gXbyyyFa-rP0tD7I'
};

// ============================================
// CHECK FOR WEBHOOK-PROVIDED FILE PARAMETERS
// ============================================

const inputData = $input.first().json;
const webhookBody = inputData.body || {};

// Check if fileId and fileName are provided via webhook
const providedFileId = webhookBody.fileId;
const providedFileName = webhookBody.fileName;

if (providedFileId && providedFileName) {
  // Use provided file parameters
  console.log(`Using provided file: ${providedFileName} (${providedFileId})`);
  return [{
    json: {
      ...testConfig,
      useProvidedFile: true,
      fileId: providedFileId,
      fileName: providedFileName,
      mimeType: 'application/pdf'
    }
  }];
} else {
  // No file provided, will pick random file
  console.log('No file provided, will select random file from test folder');

  if (testConfig.testFolderId === 'PLACEHOLDER_TEST_FOLDER_ID') {
    throw new Error('Please set testFolderId in the Set Test Config node!');
  }

  return [{
    json: {
      ...testConfig,
      useProvidedFile: false
    }
  }];
}

// Get test config and file list
const config = $('Set Test Config').first().json;
const allFiles = $input.all();

// Filter for PDF files only (skip Excel, Word, etc.)
const pdfFiles = allFiles.filter(f => {
  const name = (f.json.name || '').toLowerCase();
  return name.endsWith('.pdf');
});

if (pdfFiles.length === 0) {
  throw new Error(`No PDF files found in test folder: ${config.testFolderId}\n\nFound ${allFiles.length} files but none are PDFs.\nPlease upload test PDFs to the folder and try again.`);
}

// Pick a RANDOM PDF file
const randomIndex = Math.floor(Math.random() * pdfFiles.length);
const file = pdfFiles[randomIndex].json;

console.log(`Selected random PDF ${randomIndex + 1} of ${pdfFiles.length}: ${file.name}`);
console.log(`(Skipped ${allFiles.length - pdfFiles.length} non-PDF files)`);

return [{
  json: {
    fileId: file.id,
    fileName: file.name,
    mimeType: file.mimeType,
    testFolderId: config.testFolderId,
    client_normalized: config.client_normalized,
    clientEmail: config.clientEmail
  }
}];

// Check if we're using a provided file or random selection
const configData = $('Set Test Config').first().json;

let fileData;

if (configData.useProvidedFile) {
  // Use file data from Set Test Config (webhook-provided)
  console.log('Using webhook-provided file');
  fileData = {
    fileId: configData.fileId,
    fileName: configData.fileName,
    mimeType: configData.mimeType,
    client_normalized: configData.client_normalized,
    clientEmail: configData.clientEmail
  };
} else {
  // Use file data from Pick Random File node
  console.log('Using randomly selected file');
  const randomFileData = $input.first().json;
  fileData = {
    fileId: randomFileData.fileId,
    fileName: randomFileData.fileName,
    mimeType: randomFileData.mimeType,
    client_normalized: randomFileData.client_normalized,
    clientEmail: randomFileData.clientEmail
  };
}

console.log('Calling Chunk 2.5 with input:', JSON.stringify(fileData, null, 2));

return [{
  json: fileData
}];

const result = $input.first().json;
const testInput = $("Build Chunk 2.5 Input").first().json;

const summary = {
  "TEST COMPLETE": true,
  "File Tested": result.fileName || testInput.fileName,
  "Client": result.client_normalized || testInput.client_normalized,
  "Tier 1 Category": result.tier1Category || "N/A",
  "Tier 2 Document Type": result.documentType || "N/A",
  "Confidence": result.combinedConfidence || "N/A",
  "Tracker Column": result.mappedColumnName || "N/A",
  "Tracker Updated": result.trackerUpdated ? "Yes" : "No",
  "File Moved To": result.destinationFolderName || "N/A",
  "Status": result.chunk2_5_status || "N/A",
  "Full Result": result
};

return [{ json: summary }];

const result = $("Execute Chunk 2.5").first().json;
const testInput = $("Build Chunk 2.5 Input").first().json;

const now = new Date();
const day = String(now.getDate()).padStart(2, "0");
const month = String(now.getMonth() + 1).padStart(2, "0");
const year = now.getFullYear();
const hours = String(now.getHours()).padStart(2, "0");
const minutes = String(now.getMinutes()).padStart(2, "0");
const timestamp = day + "." + month + "." + year + " " + hours + ":" + minutes;

return [{
  json: {
    Test_Date: timestamp,
    File_Name: result.fileName || testInput.fileName || "N/A",
    Client: result.client_normalized || testInput.client_normalized || "N/A",
    Tier1_Category: result.tier1Category || "N/A",
    Tier2_DocumentType: result.documentType || "N/A",
    Confidence: result.combinedConfidence || "N/A",
    Tracker_Column: result.mappedColumnName || "N/A",
    Tracker_Updated: result.trackerUpdated ? "yes" : "no",
    File_Moved: result.destinationFolderName ? "yes" : "no",
    Final_Location: result.destinationFolderName || "N/A",
    Status: result.chunk2_5_status || "error",
    Error_Message: "",
    Tier1_Reasoning: result.tier1Reasoning || "N/A",
    Tier2_Reasoning: result.tier2Reasoning || "N/A"
  }
}];
