// V4: Prepare parent folders for batch creation
const folderConfig = $('Define Folder Structure').item.json.folderConfig;
const rootFolderId = $input.first().json.id;

const parentItems = folderConfig.parents.map(p => ({
  parentName: p.name,
  parentFolderId: rootFolderId,
  varPrefix: p.varPrefix
}));

// Add staging folder
parentItems.push({
  parentName: folderConfig.staging,
  parentFolderId: rootFolderId,
  varPrefix: 'STAGING'
});

return parentItems.map(item => ({ json: item }));

// V4: Collect parent folder IDs and prepare subfolders
const folderConfig = $('Define Folder Structure').item.json.folderConfig;

// CRITICAL FIX: Get parent folder IDs from INPUT (not from Create Parent Folder node)
// The input contains all parent folders that were created in the loop
const parentItems = $input.all();
const parentIds = {};
parentItems.forEach(item => {
  parentIds[item.json.name] = item.json.id;
});

console.log('DEBUG: parentIds map:', JSON.stringify(parentIds));

// Prepare subfolder items with their parent IDs
const subfolderItems = folderConfig.subfolders.map(sf => {
  const result = {
    subfolderName: sf.name,
    parentFolderId: parentIds[sf.parent],
    parentName: sf.parent,
    varName: sf.varName
  };
  console.log('DEBUG: subfolder item:', JSON.stringify(result));
  return result;
});

return subfolderItems.map(item => ({ json: item }));

