// V4: Extract email metadata and normalize field names
const email = $input.first().json;

const normalizedEmail = {
  emailId: email.id,
  threadId: email.threadId,
  from: email.from?.text || email.from || 'Unknown Sender',
  subject: email.subject || 'No Subject',
  date: email.date || new Date().toISOString(),
  hasAttachments: email.attachments?.length > 0 || false,
  attachmentCount: email.attachments?.length || 0,
  attachments: email.attachments || [],
  processedAt: new Date().toISOString()
};

return [{ json: normalizedEmail }];

// V4: Extract attachments to individual items for sequential processing
const emailData = $input.first().json;
const attachments = emailData.attachments;

if (!attachments || attachments.length === 0) {
  return [];
}

const attachmentItems = [];

for (let i = 0; i < attachments.length; i++) {
  const att = attachments[i];
  const fileName = att.filename || att.name || `attachment_${i}`;
  const mimeType = att.contentType || att.mimeType || 'application/octet-stream';
  const extension = fileName.split('.').pop()?.toLowerCase() || '';

  attachmentItems.push({
    json: {
      // Normalized field names (V4)
      fileId: null, // Will be set after upload
      fileName: fileName,
      originalFileName: fileName,
      mimeType: mimeType,
      extension: extension,
      size: att.size || 0,
      attachmentIndex: i,
      // Email context
      emailId: emailData.emailId,
      emailFrom: emailData.from,
      emailSubject: emailData.subject,
      emailDate: emailData.date,
      // Processing metadata
      processedAt: emailData.processedAt,
      isZip: extension === 'zip',
      isPdf: extension === 'pdf',
      isSupported: ['pdf', 'zip'].includes(extension)
    },
    binary: {
      data: att.binary || att
    }
  });
}

return attachmentItems;

// V4: Normalize extracted ZIP contents
const items = $input.all();
const originalItem = $('IF ZIP File').first().json;

const extractedFiles = [];

for (const item of items) {
  const fileName = item.json.fileName || 'extracted_file';
  const extension = fileName.split('.').pop()?.toLowerCase() || '';

  // Only process PDFs from ZIP
  if (extension === 'pdf') {
    extractedFiles.push({
      json: {
        fileId: null,
        fileName: fileName,
        originalFileName: fileName,
        mimeType: 'application/pdf',
        extension: extension,
        size: item.json.size || 0,
        emailId: originalItem.emailId,
        emailFrom: originalItem.emailFrom,
        emailSubject: originalItem.emailSubject,
        emailDate: originalItem.emailDate,
        processedAt: new Date().toISOString(),
        isZip: false,
        isPdf: true,
        isSupported: true,
        extractedFromZip: true,
        zipFileName: originalItem.fileName
      },
      binary: item.binary
    });
  }
}

return extractedFiles;

// V4: Merge ZIP and direct PDF items
const directPdf = $('IF ZIP File').first();
const zipContents = $('Normalize ZIP Contents').all();

// If this is a direct PDF (not ZIP), pass through
if (!directPdf.json.isZip) {
  return [directPdf];
}

// Otherwise, return extracted PDFs from ZIP
return zipContents;

// V4: Normalize output with fileId from upload
const uploadResult = $input.first().json;
const originalData = $('Merge File Streams').first().json;

return [{
  json: {
    // Normalized field names (V4 standard)
    fileId: uploadResult.id,
    fileName: originalData.fileName,
    originalFileName: originalData.originalFileName,
    mimeType: originalData.mimeType,
    extension: originalData.extension,
    size: originalData.size,
    // Email context
    emailId: originalData.emailId,
    emailFrom: originalData.emailFrom,
    emailSubject: originalData.emailSubject,
    emailDate: originalData.emailDate,
    // Processing metadata
    processedAt: new Date().toISOString(),
    stagingPath: uploadResult.name,
    extractedFromZip: originalData.extractedFromZip || false,
    zipFileName: originalData.zipFileName || null,
    // Placeholder fields for downstream chunks
    documentType: null,
    confidence: null,
    projectName: null
  }
}];
