// Comprehensive Test Data Generator with Edge Cases
const input = $input.first().json.body || $input.first().json;

const scenarios = {
  'HP-01': { name: 'New Client Valid PDF', clientName: 'Test Corp Alpha ' + Date.now(), clientExists: false, simulateError: null },
  'HP-02': { name: 'Existing Client', clientName: 'Eugene Wei', clientExists: true, simulateError: null },
  'EC-01': { name: 'Corrupted PDF', clientName: null, simulateError: 'CORRUPTED_FILE', expectError: true },
  'EC-02': { name: 'No Client Name', clientName: null, simulateError: 'MISSING_CLIENT_NAME', expectError: true },
  'EC-08': { name: 'Special Characters', clientName: "O'Brien Muller GmbH " + Date.now(), clientExists: false, simulateError: null }
};

const scenarioId = input.scenarioId || 'HP-01';
const scenario = scenarios[scenarioId] || scenarios['HP-01'];

return { json: {
  testId: 'test_' + Date.now(),
  scenarioId,
  scenarioName: scenario.name,
  clientName: scenario.clientName,
  clientExists: scenario.clientExists || false,
  simulateError: scenario.simulateError,
  expectError: scenario.expectError || false,
  parentFolderId: '1ikw3k-EgpVg_h2ySDdqdUFB7-FQyYDFm',
  clientRegistrySheet: '1T-jL-cLgplVeZ3EMroQxvGEqI5G7t81jRZ2qINDvXNI',
  testStartTime: new Date().toISOString()
}};

const data = $input.first().json;

// For error simulation scenarios, add error handling metadata
if (data.simulateError) {
  const errorResponses = {
    'CORRUPTED_FILE': { 
      handled: true, 
      action: 'quarantine', 
      message: 'Document quarantined - unreadable PDF' 
    },
    'MISSING_CLIENT_NAME': { 
      handled: true, 
      action: 'manual_review', 
      message: 'Routed for manual review - no client name' 
    }
  };

  data.errorHandling = errorResponses[data.simulateError] || { 
    handled: false, 
    action: 'unknown' 
  };
  data.testPhase = 'error_simulation';
} else {
  data.testPhase = 'normal_execution';
}

return { json: data };

// Pass data through only for normal scenarios
// For error scenarios, return empty to skip Chunk 0
const data = $input.first().json;

if (data.simulateError) {
  // Error scenario - return empty array to skip downstream nodes
  return [];
} else {
  // Normal scenario - pass to Chunk 0
  return { json: data };
}

// Comprehensive Cross-Platform Verification
const allInputs = $input.all();
const startData = $('Prepare Test Data').first().json;

// Get data from Merge node (contains registry lookup result)
const registryData = allInputs[0]?.json;

const errorSimulated = !!registryData?.errorHandling;
const expectedError = startData.expectError === true;

// Build comprehensive report
const report = {
  testId: startData.testId,
  scenario: {
    id: startData.scenarioId,
    name: startData.scenarioName,
    clientName: startData.clientName
  },
  timing: {
    start: startData.testStartTime,
    end: new Date().toISOString()
  },

  // Execution path
  executionPath: errorSimulated ? 'error_simulation' : 'normal_execution',

  // Platform verifications
  verifications: {
    n8n: {
      workflowExecuted: true,
      chunk0Ran: !errorSimulated,
      errorHandled: errorSimulated ? registryData.errorHandling?.handled : null
    },
    googleSheets: {
      registryChecked: !errorSimulated,
      clientFound: !!registryData?.client_name_raw,
      clientName: registryData?.client_name_raw || null
    },
    googleDrive: {
      folderId: registryData?.root_folder_id || null,
      folderCreated: !!registryData?.root_folder_id
    }
  },

  // Error handling (if applicable)
  errorHandling: registryData?.errorHandling ? {
    type: startData.simulateError,
    handled: registryData.errorHandling?.handled,
    action: registryData.errorHandling?.action,
    message: registryData.errorHandling?.message
  } : null
};

// Calculate status
if (expectedError) {
  report.status = registryData?.errorHandling?.handled ? 'PASS' : 'FAIL';
  report.statusReason = report.status === 'PASS' 
    ? 'Error correctly handled as expected' 
    : 'Error was not handled properly';
} else {
  const checks = [
    report.verifications.n8n.chunk0Ran,
    report.verifications.googleDrive.folderCreated,
    report.verifications.googleSheets.clientFound
  ];
  report.status = checks.every(c => c) ? 'PASS' : 'FAIL';
  report.statusReason = report.status === 'PASS'
    ? 'All verifications passed'
    : 'One or more verifications failed';
}

return { json: report };
