const trackerData = $('Lookup Client in Client_Tracker').all();
const mainData = $('Parse Classification Result').first().json;

const clientNormalized = mainData.clientNormalized || '';
const documentType = mainData.documentType || '';
const confidence = mainData.documentClassificationConfidence || 0;

// Check if confidence is too low
if (confidence < 70) {
  return {
    json: {
      ...mainData,
      chunk2_5_status: 'error_unclear_type',
      errorMessage: `Low confidence (${confidence}%) - document type unclear`
    }
  };
}

// Check if any data was returned
if (!trackerData || trackerData.length === 0) {
  return {
    json: {
      ...mainData,
      chunk2_5_status: 'error_client_not_found',
      errorMessage: 'No data returned from Client_Tracker lookup'
    }
  };
}

// Google Sheets lookup returns matching rows (no header in result)
// If we got results, the first row is the client data
const clientRow = trackerData[0];

// Validate that we have a Client_Name field
if (!clientRow.json.Client_Name) {
  return {
    json: {
      ...mainData,
      chunk2_5_status: 'error_client_not_found',
      errorMessage: 'Client_Tracker returned invalid data (no Client_Name field)'
    }
  };
}

// Verify this is the correct client by comparing normalized names
const sheetClientName = clientRow.json.Client_Name || '';
const normalizedSheetName = sheetClientName
  .toLowerCase()
  .trim()
  .replace(/ä/g, 'ae')
  .replace(/ö/g, 'oe')
  .replace(/ü/g, 'ue')
  .replace(/ß/g, 'ss')
  .replace(/\s*(gmbh|ag|kg|e\.v\.|mbh|co\.|&\s*co\.?)\s*/gi, '')
  .replace(/[^a-z0-9]/g, '_')
  .replace(/_+/g, '_')
  .replace(/^_|_$/g, '');

if (normalizedSheetName !== clientNormalized) {
  return {
    json: {
      ...mainData,
      chunk2_5_status: 'error_client_not_found',
      errorMessage: `Client name mismatch: expected "${clientNormalized}", got "${normalizedSheetName}"`
    }
  };
}

// Client found and validated - prepare for update
return {
  json: {
    ...mainData,
    trackerRowIndex: 0, // First row in lookup result
    trackerClientName: clientRow.json.Client_Name,
    chunk2_5_status: 'success'
  }
};

// Get Destination Folder ID - V8 Extended Mapping

const data = $input.first().json;
const actionType = data.actionType;
const documentType = data.documentType;
const tier1Category = data.tier1Category;

// Get folder IDs from AMA_Folder_IDs sheet
const folderData = data; // Folder IDs are already in the data from sheets-3

let destinationFolderId;
let destinationFolderName;

// === LOW_CONFIDENCE → 38_Unknowns ===
if (actionType === 'LOW_CONFIDENCE') {
  destinationFolderId = folderData.FOLDER_38_Unknowns;
  destinationFolderName = '38_Unknowns';
}

// === CORE TYPES → Specific Folders ===
else if (actionType === 'CORE') {
  const coreMapping = {
    '01_Projektbeschreibung': { id: 'FOLDER_01_Expose', name: '01_Expose' },
    '03_Grundbuchauszug': { id: 'FOLDER_02_Grundbuch', name: '02_Grundbuch' },
    '10_Bautraegerkalkulation_DIN276': { id: 'FOLDER_03_Calculation', name: '03_Calculation' },
    '36_Exit_Strategie': { id: 'FOLDER_04_Exit_Strategy', name: '04_Exit_Strategy' }
  };

  const mapping = coreMapping[documentType];
  if (mapping) {
    destinationFolderId = folderData[mapping.id];
    destinationFolderName = mapping.name;
  } else {
    // Fallback: should not happen if isCoreType is correct
    destinationFolderId = folderData.FOLDER_38_Unknowns;
    destinationFolderName = '38_Unknowns (CORE mapping error)';
  }
}

// === SECONDARY TYPES → Holding Folders ===
else if (actionType === 'SECONDARY') {
  // Map based on Tier 1 category to corresponding holding folder
  const holdingMapping = {
    'OBJEKTUNTERLAGEN': { id: 'FOLDER_HOLDING_PROPERTY', name: '_Holding_Property' },
    'WIRTSCHAFTLICHE_UNTERLAGEN': { id: 'FOLDER_HOLDING_FINANCIAL', name: '_Holding_Financial' },
    'RECHTLICHE_UNTERLAGEN': { id: 'FOLDER_HOLDING_LEGAL', name: '_Holding_Legal' },
    'SONSTIGES': { id: 'FOLDER_HOLDING_MISC', name: '_Holding_Misc' }
  };

  const mapping = holdingMapping[tier1Category];
  if (mapping) {
    destinationFolderId = folderData[mapping.id];
    destinationFolderName = mapping.name;
  } else {
    // Fallback
    destinationFolderId = folderData.FOLDER_38_Unknowns;
    destinationFolderName = '38_Unknowns (SECONDARY mapping error)';
  }
}

// Unknown action type fallback
else {
  destinationFolderId = folderData.FOLDER_38_Unknowns;
  destinationFolderName = '38_Unknowns (Unknown action type)';
}

return {
  json: {
    destinationFolderId,
    destinationFolderName,
    ...data
  }
};

