for (const row of dataRows) {
  const rowData = row.json;
  for (const key in rowData) {
    if (key.includes('38_Unknowns')) {
      unknownsFolderId = rowData[key];
      break;
    }
  }
  if (unknownsFolderId) break;
}

if (!unknownsFolderId) {
  // Try to get from first row (assuming it might be a global folder)
  unknownsFolderId = dataRows[0].json['38_Unknowns'] || null;
}

return {
  json: {
    ...mainData,
    unknownsFolderId: unknownsFolderId,
    errorFallbackUsed: !unknownsFolderId
  }
};

const mainData = $input.first().json;

