// CONDITIONAL: Only prepare tracker data if trackerUpdate === true
if (data.trackerUpdate !== true) {
  // SECONDARY or LOW_CONFIDENCE: Skip tracker update, pass through to folder move
  return {
    json: {
      skipTrackerUpdate: true,
      ...data
    }
  };
}

// CORE types: Prepare tracker update data
const documentType = data.documentType;

// Map document type to tracker column
const trackerColumnMapping = {
  '01_Projektbeschreibung': 'Status_Expose',
  '03_Grundbuchauszug': 'Status_Grundbuch',
  '10_Bautraegerkalkulation_DIN276': 'Status_Calculation',
  '36_Exit_Strategie': 'Status_Exit_Strategy'
};

const trackerColumn = trackerColumnMapping[documentType];

if (!trackerColumn) {
  // Should not happen if CORE mapping is correct
  return {
    json: {
      error: 'Unknown CORE document type for tracker mapping',
      skipTrackerUpdate: true,
      ...data
    }
  };
}

// Prepare update data for Client_Tracker
const updateData = {
  [trackerColumn]: '✓'
};

return {
  json: {
    trackerColumn,
    trackerValue: '✓',
    updateData,
    skipTrackerUpdate: false,
    ...data
  }
};

// Parse Tier 2 Classification Result from Claude Vision

// Get Claude Tier 2 parsed response
const tier2Result = $('Parse Claude Tier 2 Response').first().json;

if (!tier2Result || !tier2Result.documentType) {
  // Parsing failed - route to LOW_CONFIDENCE
  return [{
    json: {
      error: 'Failed to parse Tier 2 classification',
      lowConfidence: true,
      confidenceFailureStage: 'tier2_parse_error',
      ...tier2Result
    }
  }];
}

const {
  documentType,
  tier2Confidence,
  germanName,
  englishName,
  isCoreType,
  tier2Reasoning,
  fileId,
  fileName,
  fileUrl,
  clientEmail,
  tier1Category,
  tier1Confidence,
  tier1Reasoning
} = tier2Result;

// THRESHOLD CHECK: Tier 2 confidence must be >= 70%
if (tier2Confidence < 70) {
  return [{
    json: {
      documentType,
      tier2Confidence,
      lowConfidence: true,
      confidenceFailureStage: 'tier2',
      fileId,
      fileName,
      fileUrl,
      clientEmail,
      tier1Category,
      tier1Confidence,
      tier1Reasoning
    }
  }];
}

// Calculate combined confidence
const combinedConfidence = Math.round((tier1Confidence + tier2Confidence) / 2);

return [{
  json: {
    fileId,
    fileName,
    fileUrl,
    clientEmail,
    tier1Category,
    tier1Confidence,
    tier1Reasoning,
    documentType,
    tier2Confidence,
    combinedConfidence,
    germanName,
    englishName,
    isCoreType,
    tier2Reasoning
  }
}];

// Action Mapper: Determine routing based on classification results

const data = $input.first().json;

// Check for low confidence flags
if (data.lowConfidence === true) {
  return [{
    json: {
      ...data,  // ← CRITICAL: Preserve ALL incoming fields (fileId, fileName, etc.)
      actionType: 'LOW_CONFIDENCE',
      destinationFolder: '38_Unknowns',
      trackerUpdate: false,
      sendNotification: true
    }
  }];
}

// Check if CORE type
if (data.isCoreType === true) {
  // CORE types: Specific folder + tracker update
  return [{
    json: {
      ...data,  // ← CRITICAL: Preserve ALL incoming fields (fileId, fileName, etc.)
      actionType: 'CORE',
      trackerUpdate: true,
      sendNotification: false
    }
  }];
}

// SECONDARY types: Holding folder + no tracker
return [{
  json: {
    ...data,  // ← CRITICAL: Preserve ALL incoming fields (fileId, fileName, etc.)
    actionType: 'SECONDARY',
    trackerUpdate: false,
    sendNotification: false
  }
}];

const item = $input.first();
const binaryKey = Object.keys(item.binary)[0];
const buffer = await this.helpers.getBinaryDataBuffer(0, binaryKey);
const base64Content = buffer.toString('base64');
const mimeType = item.json.mimeType || 'application/pdf';

return {
  json: {
    ...item.json,
    imageData: {
      type: 'base64',
      media_type: mimeType,
      data: base64Content
    }
  },
  binary: item.binary
};

const items = $input.all();
const outputItems = [];

for (const item of items) {
  const tier1Prompt = $('Build AI Classification Prompt').first().json.tier1Prompt;
  const imageData = item.json.imageData;

  if (!imageData || !imageData.data) {
    throw new Error('No image data found in item');
  }

  const requestBody = {
    model: 'claude-sonnet-4-20250514',
    max_tokens: 200,
    messages: [
      {
        role: 'user',
        content: [
          {
            type: 'document',
            source: {
              type: 'base64',
              media_type: imageData.media_type,
              data: imageData.data
            }
          },
          {
            type: 'text',
            text: tier1Prompt
          }
        ]
      }
    ]
  };

  outputItems.push({
    json: {
      ...item.json,
      claudeRequestBody: requestBody
    },
    binary: item.binary
  });
}

return outputItems;

const items = $input.all();
const outputItems = [];

for (const item of items) {
  const response = item.json;

  if (!response.content || !response.content[0] || !response.content[0].text) {
    throw new Error('Invalid Claude API response structure');
  }

  let textContent = response.content[0].text.trim();

  // Strip markdown code fences if present
  if (textContent.startsWith('```json')) {
    textContent = textContent
      .replace(/^```json\s*\n/, '')
      .replace(/\n```\s*$/, '');
  } else if (textContent.startsWith('```')) {
    textContent = textContent
      .replace(/^```\s*\n/, '')
      .replace(/\n```\s*$/, '');
  }

  const parsedResult = JSON.parse(textContent);

  outputItems.push({
    json: {
      tier1Category: parsedResult.tier1Category,
      tier1Confidence: parsedResult.tier1Confidence,
      tier1Reasoning: parsedResult.reasoning || ''
    }
  });
}

return outputItems;

