async function pageFunction(context) {
    const { request, log } = context;
    const url = request.url;

    // REGION FILTERING
    if (url.includes('baliexception.com')) {
        const baliRegions = ['/uluwatu/', '/legian/', '/canggu/', '/ubud/', '/seminyak/', '/karangasem/', '/tabanan/', '/bingin/', '/pererenan/', '/sanur/', '/denpasar/', '/jimbaran/', '/nusa-dua/', '/kerobokan/', '/berawa/', '/echo-beach/'];
        for (const region of baliRegions) {
            if (url.toLowerCase().includes(region)) return null;
        }
        if (!url.toLowerCase().includes('/lombok/') && !url.toLowerCase().includes('lombok')) return null;
    }
    if (url.includes('invest-lombok.com') && url.toLowerCase().includes('sumbawa')) return null;
    if (url.includes('bali-home-immo.com') && !url.toLowerCase().includes('lombok')) return null;

    // EXPLICIT PROPERTY PAGE DETECTION (URL-based)
    const isPropertyPage = 
        // Task 1 sites
        (url.includes('reefpropertylombok.com') && url.includes('/projects/') && url !== 'https://reefpropertylombok.com/projects/') ||
        (url.includes('islandpropertylombok.com') && url.includes('/property/')) ||
        (url.includes('discoverlombokproperty.com') && url.includes('/listing/')) ||
        (url.includes('nourestates.com') && url.includes('/property/') && url !== 'https://www.nourestates.com/property-for-sale-kuta-lombok') ||
        // Task 2 sites (excluding South Lombok)
        (url.includes('baliexception.com') && (url.includes('/properties/for-sale/') || url.includes('/property/')) && url.toLowerCase().includes('/lombok/')) ||
        (url.includes('estate-lombok.com') && (url.includes('/villas-for-sale-lombok/') || url.includes('/en/villa/')) && url !== 'https://www.estate-lombok.com/en/villas-for-sale-lombok') ||
        (url.includes('majuproperties.com') && (url.includes('/property/') || url.includes('/properties/')) && !url.includes('?type=') && url !== 'https://www.majuproperties.com/properties') ||
        // Task 3 sites
        (url.includes('bali-home-immo.com') && url.includes('/realestate-property/for-sale/') && url.split('/').length >= 6) ||
        (url.includes('invest-lombok.com') && url.includes('/property/') && url !== 'https://www.invest-lombok.com/property/' && !url.endsWith('/property/'));

    // EXPLICIT LISTING PAGES TO SKIP
    const isListingPage = 
        url.includes('/city/') ||
        url.includes('/search-results/') ||
        url === 'https://reefpropertylombok.com/projects/' ||
        url.includes('baliexception.com/area/lombok/jsf/') ||
        url === 'https://www.estate-lombok.com/en/villas-for-sale-lombok' ||
        url.includes('majuproperties.com/properties?') ||
        url.includes('bali-home-immo.com/realestate-property?keyword') ||
        url === 'https://www.invest-lombok.com/property/' ||
        url.includes('property-for-sale-kuta-lombok') ||
        url.includes('nourestates.com') && url.includes('?');

    if (!isPropertyPage || isListingPage) {
        log.info('SKIP:', url);
        return null;
    }

    // EXTRACT DATA
    const bodyText = document.body.innerText;
    const bodyLower = bodyText.toLowerCase();

    let title = '';
    const titleSelectors = ['h1.property-title', 'h1.entry-title', '.property-header h1', 'h1.title', '.listing-title h1', '.property-name', 'h1'];
    for (const selector of titleSelectors) {
        const elem = document.querySelector(selector);
        if (elem && elem.innerText.trim()) {
            title = elem.innerText.trim();
            break;
        }
    }

    // LAND DETECTION FIRST
    let propertyType = 'Unknown';
    if (bodyText.match(/\bland\b/i) || bodyText.match(/\blot\b/i) || bodyText.match(/\bplot\b/i) || title.match(/\bland\b/i)) {
        log.info('SKIP LAND:', url);
        return null;
    } else if (bodyLower.includes('villa')) {
        propertyType = 'Villa';
    } else if (bodyLower.includes('apartment')) {
        propertyType = 'Apartment';
    } else if (bodyLower.includes('house')) {
        propertyType = 'House';
    } else if (bodyLower.includes('resort')) {
        propertyType = 'Resort';
    } else if (bodyLower.includes('bungalow')) {
        propertyType = 'Bungalow';
    }

    let priceRaw = '';
    const priceSelectors = ['.property-price', '.price', 'span.price', 'div.price', '.listing-price', '.property-meta-price'];
    for (const selector of priceSelectors) {
        const elem = document.querySelector(selector);
        if (elem && elem.innerText.trim()) {
            priceRaw = elem.innerText.trim();
            break;
        }
    }
    if (!priceRaw) {
        const pricePatterns = [/USD\s*[\d,.]+/gi, /\$\s*[\d,]+\.?\d*/gi, /Rp\s*[\d.,]+\s*(B|Billion|M|Million|Miliar|Juta)?/gi, /IDR\s*[\d.,]+/gi, /€\s*[\d,.]+/gi, /EUR\s*[\d,.]+/gi];
        for (const pattern of pricePatterns) {
            const matches = bodyText.match(pattern);
            if (matches && matches.length > 0) {
                priceRaw = matches[0];
                break;
            }
        }
    }

    let ownership = '';
    if (bodyLower.includes('hgb')) ownership = 'HGB';
    else if (bodyLower.includes('freehold')) ownership = 'Freehold';
    else if (bodyLower.includes('leasehold')) ownership = 'Leasehold';

    let status = '';
    if (bodyLower.includes('for sale')) status = 'For Sale';
    else if (bodyLower.includes('sold')) status = 'Sold';
    else if (bodyLower.includes('for rent')) status = 'For Rent';

    const hasKuta = bodyLower.includes('kuta');
    const hasSelong = bodyLower.includes('selong');
    const hasMandalika = bodyLower.includes('mandalika');
    const hasAreGuling = bodyLower.includes('are guling') || bodyLower.includes('areguling');
    const hasSenggigi = bodyLower.includes('senggigi');
    const hasGili = bodyLower.includes('gili');
    const hasFreehold = bodyLower.includes('freehold');
    const hasLeasehold = bodyLower.includes('leasehold');

    let source = 'Unknown';
    if (url.includes('reefpropertylombok.com')) source = 'Reef Property Lombok';
    else if (url.includes('islandpropertylombok.com')) source = 'Island Properties Lombok';
    else if (url.includes('discoverlombokproperty.com')) source = 'Discover Lombok Property';
    else if (url.includes('nourestates.com')) source = 'Nour Estates';
    else if (url.includes('baliexception.com')) source = 'Bali Exception';
    else if (url.includes('estate-lombok.com')) source = 'Estate Lombok';
    else if (url.includes('majuproperties.com')) source = 'Maju Properties';
    else if (url.includes('bali-home-immo.com')) source = 'Bali Home Immo';
    else if (url.includes('invest-lombok.com')) source = 'Invest Lombok';

    log.info('✅', title.substring(0, 40), priceRaw, propertyType);

    return {
        url: url,
        title: title,
        priceRaw: priceRaw,
        location: '',
        ownership: ownership,
        status: status,
        propertyType: propertyType,
        source: source,
        hasKuta: hasKuta,
        hasSelong: hasSelong,
        hasMandalika: hasMandalika,
        hasAreGuling: hasAreGuling,
        hasSenggigi: hasSenggigi,
        hasGili: hasGili,
        hasFreehold: hasFreehold,
        hasLeasehold: hasLeasehold,
        scrapedAt: new Date().toISOString()
    };
}
