async function pageFunction(context) {
    const { request, log } = context;
    const url = request.url;

    // BALI EXCEPTION: Skip Bali regions
    if (url.includes('baliexception.com')) {
        const baliRegions = ['/uluwatu/', '/legian/', '/canggu/', '/ubud/', '/seminyak/', '/karangasem/', '/tabanan/', '/bingin/', '/pererenan/', '/sanur/', '/denpasar/', '/jimbaran/', '/nusa-dua/', '/kerobokan/', '/berawa/', '/echo-beach/'];
        for (const region of baliRegions) {
            if (url.toLowerCase().includes(region)) return null;
        }
        if (!url.toLowerCase().includes('/lombok/') && !url.toLowerCase().includes('lombok')) return null;
    }

    // INVEST LOMBOK: Skip Sumbawa
    if (url.includes('invest-lombok.com') && url.toLowerCase().includes('sumbawa')) return null;

    // BALI HOME IMMO: Require Lombok
    if (url.includes('bali-home-immo.com') && !url.toLowerCase().includes('lombok')) return null;

    // Skip common non-property pages
    const skipPatterns = ['/about', '/contact', '/blog', '/news', '/team', '/services', '/privacy', '/terms', '/cookie', '/disclaimer', '#', 'javascript:', '/wp-admin', '/wp-content', '/category/', '/tag/', '/author/', '/page/', '/feed/', '.jpg', '.png', '.pdf', '.css', '.js'];
    for (const pattern of skipPatterns) {
        if (url.includes(pattern)) return null;
    }

    const bodyText = document.body.innerText;
    const bodyLower = bodyText.toLowerCase();

    // Get title
    let title = '';
    const h1 = document.querySelector('h1');
    if (h1) title = h1.innerText.trim();

    // Get price
    let priceRaw = '';
    const pricePatterns = [/\$[\d,]+/gi, /USD\s*[\d,.]+/gi, /Rp\.?\s*[\d.,]+\s*(B|Billion|M|Million|Miliar|Juta)?/gi, /IDR\s*[\d.,]+/gi, /€[\d,.]+/gi, /EUR\s*[\d,.]+/gi];
    for (const pattern of pricePatterns) {
        const matches = bodyText.match(pattern);
        if (matches && matches.length > 0) {
            priceRaw = matches[0];
            break;
        }
    }

    // Property type
    let propertyType = 'Unknown';
    if (bodyLower.includes('land') || bodyLower.includes('lot') || bodyLower.includes('plot')) {
        propertyType = 'Land';
    } else if (bodyLower.includes('villa')) {
        propertyType = 'Villa';
    } else if (bodyLower.includes('apartment')) {
        propertyType = 'Apartment';
    } else if (bodyLower.includes('house')) {
        propertyType = 'House';
    } else if (bodyLower.includes('resort')) {
        propertyType = 'Resort';
    } else if (bodyLower.includes('bungalow')) {
        propertyType = 'Bungalow';
    }

    // Skip if no indicators or if land
    if ((!priceRaw && propertyType === 'Unknown') || propertyType === 'Land') return null;

    // Ownership
    let ownership = '';
    if (bodyLower.includes('hgb')) ownership = 'HGB';
    else if (bodyLower.includes('freehold')) ownership = 'Freehold';
    else if (bodyLower.includes('leasehold')) ownership = 'Leasehold';

    // Status
    let status = '';
    if (bodyLower.includes('for sale')) status = 'For Sale';
    else if (bodyLower.includes('sold')) status = 'Sold';
    else if (bodyLower.includes('for rent')) status = 'For Rent';

    // Location flags
    const hasKuta = bodyLower.includes('kuta');
    const hasSelong = bodyLower.includes('selong');
    const hasMandalika = bodyLower.includes('mandalika');
    const hasAreGuling = bodyLower.includes('are guling') || bodyLower.includes('areguling');
    const hasSenggigi = bodyLower.includes('senggigi');
    const hasGili = bodyLower.includes('gili');
    const hasFreehold = bodyLower.includes('freehold');
    const hasLeasehold = bodyLower.includes('leasehold');

    // Source
    let source = 'Unknown';
    if (url.includes('reefpropertylombok.com')) source = 'Reef Property Lombok';
    else if (url.includes('islandpropertylombok.com')) source = 'Island Properties Lombok';
    else if (url.includes('discoverlombokproperty.com')) source = 'Discover Lombok Property';
    else if (url.includes('nourestates.com')) source = 'Nour Estates';
    else if (url.includes('baliexception.com')) source = 'Bali Exception';
    else if (url.includes('estate-lombok.com')) source = 'Estate Lombok';
    else if (url.includes('majuproperties.com')) source = 'Maju Properties';
    else if (url.includes('bali-home-immo.com')) source = 'Bali Home Immo';
    else if (url.includes('invest-lombok.com')) source = 'Invest Lombok';

    log.info('✅ EXTRACTED:', title.substring(0, 30), '| Price:', priceRaw, '| Type:', propertyType);

    return {
        url: url,
        title: title,
        priceRaw: priceRaw,
        location: '',
        ownership: ownership,
        status: status,
        propertyType: propertyType,
        source: source,
        hasKuta: hasKuta,
        hasSelong: hasSelong,
        hasMandalika: hasMandalika,
        hasAreGuling: hasAreGuling,
        hasSenggigi: hasSenggigi,
        hasGili: hasGili,
        hasFreehold: hasFreehold,
        hasLeasehold: hasLeasehold,
        scrapedAt: new Date().toISOString()
    };
}
