async function pageFunction(context) {
    const { request, log } = context;
    const url = request.url;

    // ========================================
    // REGION FILTERING (From Task 2 & 3)
    // ========================================

    // Bali Exception: Skip Bali regions, require /lombok/ in URL
    if (url.includes('baliexception.com')) {
        const baliRegions = ['/uluwatu/', '/legian/', '/canggu/', '/ubud/', '/seminyak/', '/karangasem/', '/tabanan/', '/bingin/', '/pererenan/', '/sanur/', '/denpasar/', '/jimbaran/', '/nusa-dua/', '/kerobokan/', '/berawa/', '/echo-beach/'];
        for (const region of baliRegions) {
            if (url.toLowerCase().includes(region)) {
                return null;
            }
        }
        if (!url.toLowerCase().includes('/lombok/') && !url.toLowerCase().includes('lombok')) {
            return null;
        }
    }

    // Invest Lombok: Skip Sumbawa
    if (url.includes('invest-lombok.com') && url.toLowerCase().includes('sumbawa')) {
        return null;
    }

    // Bali Home Immo: Require Lombok
    if (url.includes('bali-home-immo.com') && !url.toLowerCase().includes('lombok')) {
        return null;
    }

    // ========================================
    // RELAXED PROPERTY PAGE DETECTION
    // ========================================

    // Skip obvious listing pages only
    const isListingPage =
        url.includes('/city/') ||
        url.includes('/search-results/') ||
        url === 'https://reefpropertylombok.com/projects/' ||
        url === 'https://www.nourestates.com/property-for-sale-kuta-lombok' ||
        url.includes('baliexception.com/area/lombok/jsf/') ||
        url === 'https://www.estate-lombok.com/en/villas-for-sale-lombok' ||
        url.includes('majuproperties.com/properties?') ||
        url.includes('bali-home-immo.com/realestate-property?') ||
        url === 'https://www.invest-lombok.com/property/';

    if (isListingPage) {
        return null;
    }

    // ========================================
    // DATA EXTRACTION
    // ========================================

    const bodyText = document.body.innerText;

    // TITLE EXTRACTION (Tasks 1 & 3 simple approach)
    const titleElement = document.querySelector('h1');
    const title = titleElement ? titleElement.innerText.trim() : '';

    // PRICE EXTRACTION (Task 2 CSS-first approach)
    let priceRaw = '';

    // Try CSS selectors first
    const priceSelectors = ['.property-price', '.price', 'span.price', 'div.price', '.listing-price', '.property-meta-price'];
    for (const selector of priceSelectors) {
        const elem = document.querySelector(selector);
        if (elem && elem.innerText.trim()) {
            priceRaw = elem.innerText.trim();
            break;
        }
    }

    // Fallback to regex (Task 1 & 3 patterns)
    if (!priceRaw) {
        const pricePatterns = [
            /\$\s*[\d,]+K?/gi,
            /USD\s*[\d,]+K?/gi,
            /Rp\.?\s*[\d.,]+\s*(B|Billion|M|Million|Miliar|Juta)?/gi,
            /IDR\s*[\d.,]+/gi,
            /€\s*[\d,.]+/gi,
            /EUR\s*[\d,.]+/gi
        ];
        for (const pattern of pricePatterns) {
            const matches = bodyText.match(pattern);
            if (matches && matches.length > 0) {
                priceRaw = matches[0];
                break;
            }
        }
    }

    // PROPERTY TYPE (Land checked LAST but NOT skipped)
    let propertyType = 'Unknown';
    if (bodyText.match(/\bvilla\b/i)) {
        propertyType = 'Villa';
    } else if (bodyText.match(/\bapartment\b/i)) {
        propertyType = 'Apartment';
    } else if (bodyText.match(/\bhouse\b/i)) {
        propertyType = 'House';
    } else if (bodyText.match(/\bresort\b/i)) {
        propertyType = 'Resort';
    } else if (bodyText.match(/\bbungalow\b/i)) {
        propertyType = 'Bungalow';
    } else if (bodyText.match(/\bland\b/i) || bodyText.match(/\blot\b/i) || bodyText.match(/\bplot\b/i)) {
        propertyType = 'Land';
    }

    // CAPTURE LAND PROPERTIES (don't skip - filter in Make.com later)
    // Removed the land skip logic

    // Skip only if no price AND no property type indicators
    if (!priceRaw && propertyType === 'Unknown') {
        return null;
    }

    // OWNERSHIP EXTRACTION
    let ownership = '';
    if (bodyText.match(/\bHGB\b/i)) {
        ownership = 'HGB';
    } else if (bodyText.match(/\bFreehold\/HGB/i)) {
        ownership = 'Freehold';
    } else if (bodyText.match(/\bFreehold\b/i)) {
        ownership = 'Freehold';
    } else if (bodyText.match(/\bLeasehold\b/i)) {
        ownership = 'Leasehold';
    }

    // STATUS EXTRACTION
    let status = '';
    if (bodyText.match(/\bFor Sale\b/i) || bodyText.match(/\bSale\b/i)) {
        status = 'For Sale';
    } else if (bodyText.match(/\bSold\b/i)) {
        status = 'Sold';
    } else if (bodyText.match(/\bFor Rent\b/i) || bodyText.match(/\bLease\b/i)) {
        status = 'For Rent';
    }

    // LOCATION FLAGS
    const bodyLower = bodyText.toLowerCase();
    const hasKuta = bodyLower.includes('kuta');
    const hasSelong = bodyLower.includes('selong');
    const hasMandalika = bodyLower.includes('mandalika');
    const hasAreGuling = bodyLower.includes('are guling') || bodyLower.includes('areguling');
    const hasSenggigi = bodyLower.includes('senggigi');
    const hasGili = bodyLower.includes('gili');
    const hasFreehold = bodyLower.includes('freehold');
    const hasLeasehold = bodyLower.includes('leasehold');

    // SOURCE DETECTION
    let source = 'Unknown';
    if (url.includes('reefpropertylombok.com')) source = 'Reef Property Lombok';
    else if (url.includes('islandpropertylombok.com')) source = 'Island Properties Lombok';
    else if (url.includes('discoverlombokproperty.com')) source = 'Discover Lombok Property';
    else if (url.includes('nourestates.com')) source = 'Nour Estates';
    else if (url.includes('baliexception.com')) source = 'Bali Exception';
    else if (url.includes('estate-lombok.com')) source = 'Estate Lombok';
    else if (url.includes('majuproperties.com')) source = 'Maju Properties';
    else if (url.includes('bali-home-immo.com')) source = 'Bali Home Immo';
    else if (url.includes('invest-lombok.com')) source = 'Invest Lombok';

    log.info('✅ EXTRACTED:', title || '(no title)', '| Price:', priceRaw || '(no price)', '| Type:', propertyType, '| Source:', source);

    return {
        url: url,
        title: title,
        priceRaw: priceRaw,
        location: '',
        ownership: ownership,
        status: status,
        propertyType: propertyType,
        source: source,
        hasKuta: hasKuta,
        hasSelong: hasSelong,
        hasMandalika: hasMandalika,
        hasAreGuling: hasAreGuling,
        hasSenggigi: hasSenggigi,
        hasGili: hasGili,
        hasFreehold: hasFreehold,
        hasLeasehold: hasLeasehold,
        scrapedAt: new Date().toISOString()
    };
}
