async function pageFunction(context) {
    const { request, log } = context;
    const url = request.url;

    // === BALI REGION REJECTION (CRITICAL FIX) ===
    // Skip Bali regions from Bali Exception - these were incorrectly scraped in previous run
    if (url.includes('baliexception.com')) {
        const baliRegions = ['/uluwatu/', '/legian/', '/canggu/', '/ubud/', 
                             '/seminyak/', '/karangasem/', '/tabanan/', '/bingin/', 
                             '/pererenan/', '/sanur/', '/denpasar/', '/jimbaran/',
                             '/nusa-dua/', '/kerobokan/', '/berawa/', '/echo-beach/'];
        for (const region of baliRegions) {
            if (url.toLowerCase().includes(region)) {
                log.info('SKIPPED (Bali region, not Lombok):', url);
                return null;
            }
        }
        // Also require 'lombok' in the URL path for Bali Exception
        if (!url.toLowerCase().includes('/lombok/') && !url.toLowerCase().includes('lombok')) {
            log.info('SKIPPED (Not a Lombok URL):', url);
            return null;
        }
    }

    // === PROPERTY PAGE DETECTION ===
    const isPropertyPage = 
        // Bali Exception: property detail pages in the filtered results (MUST be Lombok)
        (url.includes('baliexception.com') && 
         (url.includes('/properties/for-sale/') || url.includes('/property/')) &&
         url.toLowerCase().includes('/lombok/')) ||
        // Estate Lombok: villa detail pages
        (url.includes('estate-lombok.com') && 
         (url.includes('/villas-for-sale-lombok/') || url.includes('/en/villa/'))) ||
        // South Lombok Land Sales: /property/[name]/[id] format
        (url.includes('southlomboklandsales.com') && 
         url.includes('/property/') && 
         url.split('/property/')[1] && 
         url.split('/property/')[1].includes('/')) ||
        // Maju Properties: /property/[slug] or /properties/[slug]
        (url.includes('majuproperties.com') && 
         (url.includes('/property/') || url.includes('/properties/')) &&
         url !== 'https://www.majuproperties.com/properties' &&
         !url.includes('?type='));

    // === LISTING PAGES TO SKIP ===
    const isListingPage = 
        // Bali Exception filtered listing page
        url.includes('baliexception.com/area/lombok/jsf/') ||
        // Estate Lombok main listing page
        url === 'https://www.estate-lombok.com/en/villas-for-sale-lombok' ||
        url.match(/estate-lombok\.com\/en\/villas-for-sale-lombok\/?$/) ||
        // South Lombok Land Sales main listing
        url === 'https://www.southlomboklandsales.com/property' ||
        // Maju Properties listing with filters
        url.includes('majuproperties.com/properties?');

    if (!isPropertyPage || isListingPage) {
        log.info('SKIPPED (listing/non-property):', url);
        return null;
    }

    // === EXTRACT DATA WITH IMPROVED CSS SELECTORS ===
    const bodyText = document.body.innerText;

    // Try multiple selectors for title (site-specific)
    let title = '';
    const titleSelectors = [
        'h1.property-title',
        'h1.entry-title', 
        '.property-header h1',
        'h1.title',
        'h1'
    ];
    for (const selector of titleSelectors) {
        const elem = document.querySelector(selector);
        if (elem && elem.innerText.trim()) {
            title = elem.innerText.trim();
            break;
        }
    }

    // === PROPERTY TYPE DETECTION (EXCLUDE LAND) ===
    let propertyType = 'Unknown';
    if (bodyText.match(/\bvilla\b/i)) {
        propertyType = 'Villa';
    } else if (bodyText.match(/\bapartment\b/i)) {
        propertyType = 'Apartment';
    } else if (bodyText.match(/\bhouse\b/i)) {
        propertyType = 'House';
    } else if (bodyText.match(/\bresort\b/i)) {
        propertyType = 'Resort';
    } else if (bodyText.match(/\bbungalow\b/i)) {
        propertyType = 'Bungalow';
    } else if (bodyText.match(/\bland\b/i) || bodyText.match(/\blot\b/i) || bodyText.match(/\bplot\b/i)) {
        propertyType = 'Land';
    }

    // === SKIP LAND PROPERTIES (USER DOESN'T WANT THEM) ===
    if (propertyType === 'Land') {
        log.info('SKIPPED (land property):', url, '- User only wants villas/apartments');
        return null;
    }

    // === PRICE EXTRACTION (Multi-format with CSS selectors first) ===
    let priceRaw = '';

    // Try CSS selectors first (more reliable)
    const priceSelectors = [
        '.property-price',
        '.price',
        'span.price',
        'div.price',
        '.listing-price',
        '.property-meta-price'
    ];
    for (const selector of priceSelectors) {
        const elem = document.querySelector(selector);
        if (elem && elem.innerText.trim()) {
            priceRaw = elem.innerText.trim();
            break;
        }
    }

    // Fallback to regex patterns if CSS selectors fail
    if (!priceRaw) {
        const pricePatterns = [
            /USD\s*[\d,.]+/gi,                                      // USD 285000
            /\$\s*[\d,]+\.?\d*/gi,                                  // $285,000 or $285K
            /Rp\s*[\d.,]+\s*(B|Billion|M|Million|Miliar|Juta)?/gi, // IDR: Rp 3.60 Billion
            /IDR\s*[\d.,]+/gi,                                      // IDR 3600000000
            /€\s*[\d,.]+/gi,                                        // EUR: €285,000
            /EUR\s*[\d,.]+/gi                                       // EUR 285000
        ];

        for (const pattern of pricePatterns) {
            const matches = bodyText.match(pattern);
            if (matches && matches.length > 0) {
                priceRaw = matches[0];
                break;
            }
        }
    }

    // === OWNERSHIP EXTRACTION ===
    let ownership = '';
    if (bodyText.match(/\bHGB\b/i)) {
        ownership = 'HGB';
    } else if (bodyText.match(/\bFreehold\b/i)) {
        ownership = 'Freehold';
    } else if (bodyText.match(/\bLeasehold\b/i)) {
        ownership = 'Leasehold';
    }

    // === STATUS EXTRACTION ===
    let status = '';
    if (bodyText.match(/\bFor Sale\b/i) || bodyText.match(/\bSale\b/i)) {
        status = 'For Sale';
    } else if (bodyText.match(/\bSold\b/i)) {
        status = 'Sold';
    } else if (bodyText.match(/\bFor Rent\b/i) || bodyText.match(/\bLease\b/i)) {
        status = 'For Rent';
    }

    // === LOCATION FLAGS ===
    const hasKuta = bodyText.toLowerCase().includes('kuta');
    const hasSelong = bodyText.toLowerCase().includes('selong');
    const hasMandalika = bodyText.toLowerCase().includes('mandalika');
    const hasFreehold = bodyText.toLowerCase().includes('freehold');
    const hasLeasehold = bodyText.toLowerCase().includes('leasehold');

    // === SOURCE DETECTION ===
    let source = 'Unknown';
    if (url.includes('baliexception.com')) source = 'Bali Exception';
    else if (url.includes('estate-lombok.com')) source = 'Estate Lombok';
    else if (url.includes('southlomboklandsales.com')) source = 'South Lombok Land Sales';
    else if (url.includes('majuproperties.com')) source = 'Maju Properties';

    log.info('EXTRACTED:', title, '| Price:', priceRaw, '| Type:', propertyType, '| Source:', source);

    return {
        url: url,
        title: title,
        priceRaw: priceRaw,
        ownership: ownership,
        status: status,
        propertyType: propertyType,
        source: source,
        hasKuta: hasKuta,
        hasSelong: hasSelong,
        hasMandalika: hasMandalika,
        hasFreehold: hasFreehold,
        hasLeasehold: hasLeasehold,
        scrapedAt: new Date().toISOString()
    };
}
