async function pageFunction(context) {
    const { request, log } = context;
    const url = request.url;

    // === PROPERTY PAGE DETECTION ===
    const isPropertyPage = 
        // Bali Home Immo: /realestate-property/for-sale/[type]/[ownership]/[location]/[slug]
        (url.includes('bali-home-immo.com') && 
         url.includes('/realestate-property/for-sale/') && 
         url.split('/realestate-property/for-sale/')[1] && 
         url.split('/realestate-property/for-sale/')[1].split('/').length >= 3) ||
        // Invest Lombok: /property/[slug]/ (not the listing page)
        (url.includes('invest-lombok.com') && 
         url.includes('/property/') && 
         url !== 'https://www.invest-lombok.com/property/' &&
         !url.endsWith('/property/'));

    // === LISTING PAGES TO SKIP ===
    const isListingPage = 
        // Bali Home Immo search/listing pages
        url.includes('bali-home-immo.com/realestate-property?') ||
        url === 'https://www.bali-home-immo.com/realestate-property' ||
        // Invest Lombok listing page
        url === 'https://www.invest-lombok.com/property/' ||
        url === 'https://www.invest-lombok.com/' ||
        url.includes('invest-lombok.com/property-type/');

    // === SKIP NON-LOMBOK REGIONS ===
    const bodyText = document.body.innerText.toLowerCase();
    const isNonLombok = 
        // Bali Home Immo: Check Sub Area field or URL
        (url.includes('bali-home-immo.com') && 
         !url.toLowerCase().includes('lombok') && 
         !bodyText.includes('sub area') && 
         !bodyText.includes('lombok')) ||
        // Invest Lombok: Skip Sumbawa properties
        (url.includes('invest-lombok.com') && 
         (url.toLowerCase().includes('sumbawa') || 
          bodyText.includes('sumbawa') || 
          bodyText.includes('sekongkang')));

    if (isNonLombok) {
        log.info('SKIPPED (Non-Lombok region):', url);
        return null;
    }

    if (!isPropertyPage || isListingPage) {
        log.info('SKIPPED (listing/non-property):', url);
        return null;
    }

    // === EXTRACT DATA ===
    const fullBodyText = document.body.innerText;
    const titleElement = document.querySelector('h1');
    const title = titleElement ? titleElement.innerText.trim() : '';

    // === PRICE EXTRACTION (Multi-format) ===
    let priceRaw = '';
    const pricePatterns = [
        /\$[\d,]+\.?\d*/gi,                                     // USD: $690,000.00
        /USD\s*[\d,.]+/gi,                                       // USD 690,000
        /IDR\s*[\d.,]+/gi,                                       // IDR 4.948.000.000
        /Rp\.?\s*[\d.,]+\s*(B|Billion|M|Million|Miliar|Juta)?/gi, // Rp 4.948.000.000
        /[\d.,]+\s*(IDR|Rupiah)/gi                               // 4.948.000.000 IDR
    ];
    for (const pattern of pricePatterns) {
        const matches = fullBodyText.match(pattern);
        if (matches && matches.length > 0) {
            priceRaw = matches[0];
            break;
        }
    }

    // === OWNERSHIP EXTRACTION ===
    let ownership = '';
    if (fullBodyText.match(/\bHGB\b/i)) ownership = 'HGB';
    else if (fullBodyText.match(/\bFreehold\b/i)) ownership = 'Freehold';
    else if (fullBodyText.match(/\bLeasehold\b/i)) ownership = 'Leasehold';

    // === STATUS EXTRACTION ===
    let status = '';
    if (fullBodyText.match(/\bFor Sale\b/i) || fullBodyText.match(/\bSale\b/i)) status = 'For Sale';
    else if (fullBodyText.match(/\bSold\b/i)) status = 'Sold';
    else if (fullBodyText.match(/\bFor Rent\b/i) || fullBodyText.match(/\bLease\b/i)) status = 'For Rent/Lease';

    // === PROPERTY TYPE ===
    let propertyType = 'Unknown';
    if (fullBodyText.match(/\bvilla\b/i)) propertyType = 'Villa';
    else if (fullBodyText.match(/\bbungalow\b/i)) propertyType = 'Villa';
    else if (fullBodyText.match(/\bresort\b/i)) propertyType = 'Resort';
    else if (fullBodyText.match(/\bland\b/i) || fullBodyText.match(/\blot\b/i)) propertyType = 'Land';
    else if (fullBodyText.match(/\bhouse\b/i)) propertyType = 'House';

    // === LOCATION FLAGS ===
    const hasKuta = fullBodyText.toLowerCase().includes('kuta');
    const hasSelong = fullBodyText.toLowerCase().includes('selong');
    const hasAreGuling = fullBodyText.toLowerCase().includes('are guling');
    const hasSenggigi = fullBodyText.toLowerCase().includes('senggigi');
    const hasGili = fullBodyText.toLowerCase().includes('gili');
    const hasFreehold = fullBodyText.toLowerCase().includes('freehold');
    const hasLeasehold = fullBodyText.toLowerCase().includes('leasehold');

    // === SOURCE DETECTION ===
    let source = 'Unknown';
    if (url.includes('bali-home-immo.com')) source = 'Bali Home Immo';
    else if (url.includes('invest-lombok.com')) source = 'Invest Lombok';

    log.info('EXTRACTED:', title, '| Price:', priceRaw, '| Source:', source);

    return {
        url: url,
        title: title,
        priceRaw: priceRaw,
        ownership: ownership,
        status: status,
        propertyType: propertyType,
        source: source,
        hasKuta: hasKuta,
        hasSelong: hasSelong,
        hasAreGuling: hasAreGuling,
        hasSenggigi: hasSenggigi,
        hasGili: hasGili,
        hasFreehold: hasFreehold,
        hasLeasehold: hasLeasehold,
        scrapedAt: new Date().toISOString()
    };
}
