// Parse and validate Month/Year input
const input = $input.first().json;
const monthYear = input.month_year || "September 2025";

const match = monthYear.match(/^([A-Za-z]+)\s+(\d{4})$/);
if (!match) throw new Error('Format: "Month YYYY"');

const month = match[1].charAt(0).toUpperCase() + match[1].slice(1).toLowerCase();
const year = match[2];

return {
  json: {
    month,
    year,
    folder_name: `VAT ${month} ${year}`,
    base_folder_id: "1fgJLso3FuZxX6JjUtN_PHsrIc-VCyl15"
  }
};

// Prepare data for creating 4 bank folders
const mainFolder = $input.first().json;
const banks = ['ING Diba', 'Deutsche Bank', 'Barclays', 'Mastercard'];

return banks.map(bank => ({
  json: {
    bank_name: bank,
    main_vat_folder_id: mainFolder.id,
    month: mainFolder.name.split(' ')[1],
    year: mainFolder.name.split(' ')[2]
  }
}));

// Get main VAT folder info
const mainFolderId = $('Create Main VAT Folder').first().json.id;
const folderName = $('Create Main VAT Folder').first().json.name;
const [_, month, year] = folderName.split(' ');

return {
  json: {
    main_vat_folder_id: mainFolderId,
    month,
    year
  }
};

// Process statements and prepare for moving
const statements = $input.all();
const month = $('Get Main Folder Data').first().json.month;
const year = $('Get Main Folder Data').first().json.year;

// Bank folder mapping
const bankFolders = {
  'ING Diba': null,
  'Deutsche Bank': null,
  'Barclays': null,
  'Mastercard': null
};

// Get bank folder IDs from created folders
const createdBankFolders = $('Create Bank Folder').all();
createdBankFolders.forEach(folder => {
  const bankName = folder.json.bank_name;
  if (bankFolders.hasOwnProperty(bankName)) {
    bankFolders[bankName] = folder.json.id;
  }
});

// Process each statement
const results = [];
for (const statement of statements) {
  const bank = statement.json.Bank;
  const fileId = statement.json.FileID;
  const statementId = statement.json.StatementID;

  if (!bank || !fileId) {
    results.push({
      json: {
        error: 'Missing Bank or FileID',
        StatementID: statementId,
        skipped: true
      }
    });
    continue;
  }

  const bankFolderId = bankFolders[bank];
  if (!bankFolderId) {
    results.push({
      json: {
        error: `Bank folder not found: ${bank}`,
        StatementID: statementId,
        skipped: true
      }
    });
    continue;
  }

  // Find Statements subfolder for this bank
  const statementsSubfolder = $('Create Statements Subfolder').all()
    .find(f => true);

  results.push({
    json: {
      StatementID: statementId,
      fileId,
      bank,
      bankFolderId,
      targetFolderId: statementsSubfolder?.json.id || bankFolderId,
      month,
      year,
      FilePath: `VAT ${month} ${year}/${bank}/Statements/`
    }
  });
}

return results.length > 0 ? results : [{ json: { no_statements: true } }];

