const receipts = $input.all();
const transactions = $('Read Transactions Sheet').all();
const month = $('Get Main Folder Data').first().json.month;
const year = $('Get Main Folder Data').first().json.year;

// Create transaction lookup map
const transactionMap = {};
transactions.forEach(t => {
  transactionMap[t.json.TransactionID] = t.json.Bank;
});

// Bank folder mapping
const bankFolders = {
  'ING Diba': null,
  'Deutsche Bank': null,
  'Barclays': null,
  'Mastercard': null
};

const createdBankFolders = $('Create Bank Folder').all();
createdBankFolders.forEach(folder => {
  const bankName = folder.json.bank_name;
  if (bankFolders.hasOwnProperty(bankName)) {
    bankFolders[bankName] = folder.json.id;
  }
});

// Process each receipt
const results = [];
for (const receipt of receipts) {
  const transactionId = receipt.json.transaction_id;
  const receiptId = receipt.json.ReceiptID;
  const fileId = receipt.json.FileID;

  if (!transactionId) {
    results.push({
      json: {
        error: 'No transaction_id',
        ReceiptID: receiptId,
        skipped: true
      }
    });
    continue;
  }

  const bank = transactionMap[transactionId];
  if (!bank) {
    results.push({
      json: {
        error: `Transaction not found: ${transactionId}`,
        ReceiptID: receiptId,
        skipped: true
      }
    });
    continue;
  }

  const bankFolderId = bankFolders[bank];
  if (!bankFolderId) {
    results.push({
      json: {
        error: `Bank folder not found: ${bank}`,
        ReceiptID: receiptId,
        skipped: true
      }
    });
    continue;
  }

  results.push({
    json: {
      ReceiptID: receiptId,
      fileId,
      bank,
      targetFolderId: bankFolderId,
      month,
      year,
      FilePath: `VAT ${month} ${year}/${bank}/Receipts/`
    }
  });
}

return results.length > 0 ? results : [{ json: { no_receipts: true } }];

// Collect all items from merge node
const allItems = $input.all();

// Parse month/year from initial input
const monthYear = $node["Parse Month/Year Input"].json;
const mainFolder = $node["Create Income Folder"].json;

// Count statements, receipts, and invoices processed
const statementItems = allItems.filter(item => 
  item.json.StatementID || item.json.BankName
);
const receiptItems = allItems.filter(item => 
  item.json.ReceiptID && !item.json.StatementID
);
const invoiceItems = allItems.filter(item => 
  item.json.InvoiceID && !item.json.ReceiptID
);

const statementsProcessed = statementItems.length;
const receiptsProcessed = receiptItems.length;
const invoicesProcessed = invoiceItems.length;

// Get folder structure info
const folderPath = `VAT ${monthYear.year}/${monthYear.month}`;
const incomePath = `${folderPath}/Income`;

// Generate summary message
const summary = {
  success: true,
  month: monthYear.month,
  year: monthYear.year,
  folderPath: folderPath,
  incomeFolderPath: incomePath,
  mainFolderId: mainFolder.id,
  statistics: {
    statementsProcessed,
    receiptsProcessed,
    invoicesProcessed,
    totalFilesOrganized: statementsProcessed + receiptsProcessed + invoicesProcessed
  },
  message: `Successfully organized ${monthYear.month} ${monthYear.year}:\n` +
    `- ${statementsProcessed} bank statements\n` +
    `- ${receiptsProcessed} expense receipts\n` +
    `- ${invoicesProcessed} invoices\n` +
    `Total: ${statementsProcessed + receiptsProcessed + invoicesProcessed} files organized`
};

return [{ json: summary }];

const targetMonth = $node["Parse Month/Year Input"].json.month;
const targetYear = $node["Parse Month/Year Input"].json.year;
const incomeFolder = $node["Create Income Folder"].json.id;

// Filter invoices for this month that have been matched
const invoices = $input.all().filter(invoice => {
  const invoiceDate = new Date(invoice.json.Date);
  const invoiceMonth = invoiceDate.toLocaleString('en-US', { month: 'long' });
  const invoiceYear = invoiceDate.getFullYear().toString();

  return (
    invoiceMonth === targetMonth &&
    invoiceYear === targetYear &&
    invoice.json.InvoiceFileID &&
    invoice.json.InvoiceFileID.trim() !== ""
  );
});

// Return invoices with destination folder
return invoices.map(invoice => ({
  json: {
    ...invoice.json,
    destinationFolder: incomeFolder,
    originalFileId: invoice.json.InvoiceFileID
  }
}));

